async function signupFormHandler(a){console.log("SignupFormHandler"),a.preventDefault();const b=document.querySelector("#name-signup").value.trim(),c=document.querySelector("#email-signup").value.trim(),d=document.querySelector("#password-signup").value.trim();if(b&&c&&d){const a=await fetch("/api/users",{method:"POST",body:JSON.stringify({name:b,email:c,password:d}),headers:{"Content-Type":"application/json"}});a.ok?(console.log("success"),document.location.replace("/dashboard")):alert(a.statusText)}}document.querySelector("#signup-form").addEventListener("submit",signupFormHandler);